import React from 'react';
import { BrowserRouter as Router, Route, Routes } from 'react-router-dom';
import NavbarBeforeLogin from './NavbarBeforeLogin';
import NavbarAfterLogin from './NavbarAfterLogin';
import Footer from './Footer';
import LandingPage from './LandingPage';
import AboutPage from './AboutPage';
import ContactPage from './ContactPage';
import SignUpPage from './SignUpPage';
import LoginPage from './LoginPage';
import TenantDashboard from './TenantDashboard';
import OwnerDashboard from './OwnerDashboard';
import ProtectedRoute from './ProtectedRoute'; // Assuming you have this component

function App() {
    const token = localStorage.getItem('token');
    const user = JSON.parse(localStorage.getItem('user'));

    return (
        <Router>
            <div className="min-h-screen flex flex-col">
                {/* Wrapping the whole component tree in Router */}
                <AppContent token={token} user={user} />
            </div>
        </Router>
    );
}

const AppContent = ({ token, user }) => {
    const location = useLocation();

    // Check if the current page is either TenantDashboard or OwnerDashboard
    const isDashboard = 
        location.pathname === '/tenant-dashboard' || 
        location.pathname === '/owner-dashboard';

    return (
        <>
            {/* Render Navbar only if not on dashboard pages */}
            {!isDashboard && (token && user ? <NavbarAfterLogin /> : <NavbarBeforeLogin />)}

            <div className="flex-grow">
                <Routes>
                    {/* Public Routes */}
                    <Route path="/" element={<LandingPage />} />
                    <Route path="/about" element={<AboutPage />} />
                    <Route path="/contact" element={<ContactPage />} />
                    <Route path="/signup" element={<SignUpPage />} />
                    <Route path="/login" element={<LoginPage />} />

                    {/* Protected Routes for Dashboard */}
                    <Route
                        path="/tenant-dashboard"
                        element={
                            <ProtectedRoute userType="tenant">
                                <TenantDashboard />
                            </ProtectedRoute>
                        }
                    />
                    <Route
                        path="/owner-dashboard"
                        element={
                            <ProtectedRoute userType="owner">
                                <OwnerDashboard />
                            </ProtectedRoute>
                        }
                    />

                    {/* Default Fallback Route */}
                    <Route path="*" element={<LoginPage />} />
                </Routes>
            </div>

            {/* Render Footer only if not on dashboard pages */}
            {!isDashboard && <Footer />}
        </>
    );
};

export default App;
