import React from 'react';
import { BrowserRouter as Router, Route, Routes, Navigate, useLocation } from 'react-router-dom';
import NavbarBeforeLogin from './NavbarBeforeLogin';
import NavbarAfterLogin from './NavbarAfterLogin';
import Footer from './Footer';
import LandingPage from './LandingPage';
import AboutPage from './AboutPage';
import ContactPage from './ContactPage';
import SignUpPage from './SignUpPage';
import LoginPage from './LoginPage';
import TenantDashboardLayout from './TenantDashboardLayout'; // New layout for tenant dashboard
import OwnerDashboardLayout from './OwnerDashboardLayout'; // New layout for owner dashboard

// Protected Route Component
const ProtectedRoute = ({ children, userType }) => {
    const token = localStorage.getItem('token'); // Get token from localStorage
    const user = JSON.parse(localStorage.getItem('user')); // Get user details from localStorage

    if (!token || !user) {
        // If the user is not logged in, redirect them to the login page
        return <Navigate to="/login" />;
    }

    // If userType is provided, ensure it matches the current user's type
    if (userType && user?.userType !== userType) {
        // If user type does not match, redirect to the login page
        return <Navigate to="/login" />;
    }

    return children; // If everything is fine, render the children components (dashboard)
};

function App() {
    const location = useLocation(); // Ensure this is inside the Router
    const token = localStorage.getItem('token');
    const user = JSON.parse(localStorage.getItem('user'));

    // Check if the current page is either TenantDashboard or OwnerDashboard
    const isDashboard =
        location.pathname === '/tenant-dashboard' || location.pathname === '/owner-dashboard';

    return (
        <div className="min-h-screen flex flex-col">
            {/* Conditional Navbar Rendering */}
            {!isDashboard && (token && user ? <NavbarAfterLogin /> : <NavbarBeforeLogin />)}

            <div className="flex-grow">
                <Routes>
                    {/* Public Routes */}
                    <Route path="/" element={<LandingPage />} />
                    <Route path="/about" element={<AboutPage />} />
                    <Route path="/contact" element={<ContactPage />} />
                    <Route path="/signup" element={<SignUpPage />} />
                    <Route path="/login" element={<LoginPage />} />

                    {/* Protected Routes for Dashboard */}
                    <Route
                        path="/tenant-dashboard"
                        element={
                            <ProtectedRoute userType="tenant">
                                <TenantDashboardLayout /> {/* Updated to use new tenant layout */}
                            </ProtectedRoute>
                        }
                    />
                    <Route
                        path="/owner-dashboard"
                        element={
                            <ProtectedRoute userType="owner">
                                <OwnerDashboardLayout /> {/* Updated to use new owner layout */}
                            </ProtectedRoute>
                        }
                    />

                    {/* Default Fallback Route */}
                    <Route path="*" element={<LoginPage />} />
                </Routes>
            </div>

            {/* Conditional Footer Rendering */}
            {!isDashboard && <Footer />}
        </div>
    );
}

export default function AppWrapper() {
    return (
        <Router>
            <App />
        </Router>
    );
}
