import React, { useState, useEffect } from 'react';

const SearchProperty = () => {
    const [properties, setProperties] = useState([]);

    useEffect(() => {
        // Fetch properties from the backend, for now simulating with dummy data
        const fetchProperties = async () => {
            const dummyProperties = [
                { id: 1, name: 'Apartment 1', location: 'Location 1', rent: 1000 },
                { id: 2, name: 'Apartment 2', location: 'Location 2', rent: 1200 },
            ];
            setProperties(dummyProperties);
        };
        fetchProperties();
    }, []);

    const handleInterest = (property) => {
        // This is where you will trigger an email or message to the owner
        alert(`Interest shown in ${property.name}`);
    };

    return (
        <div className="container mx-auto py-10">
            <h1 className="text-4xl mb-6">Search Properties</h1>
            <ul className="space-y-4">
                {properties.map((property) => (
                    <li key={property.id} className="p-4 border rounded">
                        <h2 className="text-xl">{property.name}</h2>
                        <p>Location: {property.location}</p>
                        <p>Rent: ${property.rent}</p>
                        <button
                            onClick={() => handleInterest(property)}
                            className="bg-customGreen text-white py-2 px-4 rounded mt-4"
                        >
                            Show Interest
                        </button>
                    </li>
                ))}
            </ul>
        </div>
    );
};

export default SearchProperty;
