import React, { useState } from 'react';
import axios from 'axios';

const SignUpPage = () => {
    const [step, setStep] = useState(1); // To track which step the user is on
    const [userType, setUserType] = useState(''); // To track whether user selects Tenant or Owner
    const [formData, setFormData] = useState({
        firstName: '',
        lastName: '',
        phone: '',
        email: '',
        otp: '',
        password: '',
        confirmPassword: ''
    });

    const [errors, setErrors] = useState({});
    const [loading, setLoading] = useState(false); // Track loading state
    const [message, setMessage] = useState(''); // Success or Error messages
    const [otpVerified, setOtpVerified] = useState(false); // Track OTP verification

    // Simple validation function
    const validateInputs = () => {
        let valid = true;
        let newErrors = {};

        // Validate first name
        if (formData.firstName === '') {
            newErrors.firstName = 'First Name is required';
            valid = false;
        }

        // Validate last name
        if (formData.lastName === '') {
            newErrors.lastName = 'Last Name is required';
            valid = false;
        }

        // Validate phone (example: 10 digits)
        const phonePattern = /^[0-9]{10}$/;
        if (!phonePattern.test(formData.phone)) {
            newErrors.phone = 'Phone number must be 10 digits';
            valid = false;
        }

        // Validate email
        const emailPattern = /^[^\s@]+@[^\s@]+\.[^\s@]+$/;
        if (!emailPattern.test(formData.email)) {
            newErrors.email = 'Email is not valid';
            valid = false;
        }

        setErrors(newErrors);
        return valid;
    };

    // Function to handle OTP verification
    const handleVerify = async () => {
        setLoading(true);
        try {
            // Verify OTP with backend
            const response = await axios.post('http://localhost:5000/api/auth/verify-otp', {
                email: formData.email,
                otp: formData.otp,
            });
            console.log(response.data); // You can log the success response here
            setMessage('OTP verified! You can now create a password.');
            setOtpVerified(true); // OTP is verified, allow password creation
            setStep(4); // Move to password creation step
        } catch (error) {
            setMessage('OTP verification failed, please try again.');
            console.log('Error verifying OTP:', error.response.data.msg);
        }
        setLoading(false);
    };

    // Password Validation
    const validatePassword = () => {
        let valid = true;
        let newErrors = {};

        // Validate password length
        if (formData.password.length < 8) {
            newErrors.password = 'Password must be at least 8 characters long';
            valid = false;
        }

        // Validate confirm password matches
        if (formData.password !== formData.confirmPassword) {
            newErrors.confirmPassword = 'Passwords do not match';
            valid = false;
        }

        setErrors(newErrors);
        return valid;
    };

    // Function to handle password setting
    const handleSetPassword = async () => {
        if (validatePassword()) {
            setLoading(true);
            try {
                // Send final data to backend
                const response = await axios.post('http://localhost:5000/api/auth/set-password', {
                    email: formData.email,
                    password: formData.password,
                    userType: userType,
                });
                console.log(response.data); // Log the success response
                setMessage('Password set successfully!');
            } catch (error) {
                setMessage('Error setting password, please try again.');
                console.log('Error setting password:', error.response.data.msg);
            }
            setLoading(false);
        }
    };

    // Handle form input changes
    const handleInputChange = (e) => {
        setFormData({ ...formData, [e.target.name]: e.target.value });
    };

    return (
        <div className="bg-gray-100 py-10 min-h-screen">
            <div className="max-w-3xl mx-auto px-6">
                {step === 4 ? (
                    // Step 4: Password Creation
                    <div className="bg-white p-10 shadow-xl rounded-lg mb-10">
                        <h1 className="text-4xl text-customGreen font-bold mb-6 text-center">Create Password</h1>
                        {message && <p className="text-green-500 text-center">{message}</p>}

                        <div className="mb-6">
                            <label className="block text-lg text-gray-600 mb-2">Password</label>
                            <input
                                type="password"
                                name="password"
                                value={formData.password}
                                onChange={handleInputChange}
                                className="w-full border border-gray-300 rounded-lg p-3"
                            />
                            {errors.password && <p className="text-red-500 text-sm">{errors.password}</p>}
                        </div>

                        <div className="mb-6">
                            <label className="block text-lg text-gray-600 mb-2">Confirm Password</label>
                            <input
                                type="password"
                                name="confirmPassword"
                                value={formData.confirmPassword}
                                onChange={handleInputChange}
                                className="w-full border border-gray-300 rounded-lg p-3"
                            />
                            {errors.confirmPassword && <p className="text-red-500 text-sm">{errors.confirmPassword}</p>}
                        </div>

                        <button
                            type="button"
                            onClick={handleSetPassword}
                            className="w-full bg-customGreen text-white py-3 rounded-lg hover:bg-green-600"
                            disabled={loading} // Disable button while loading
                        >
                            {loading ? 'Setting Password...' : 'Set Password'}
                        </button>
                    </div>
                ) : (
                    <div className="bg-white p-10 shadow-xl rounded-lg mb-10">
                        <h1 className="text-4xl text-customGreen font-bold mb-6 text-center">OTP Verification</h1>
                        {message && <p className="text-green-500 text-center">{message}</p>}
                        <p className="text-lg text-gray-600 mb-6 text-center">
                            An OTP has been sent to your email ID: <strong>{formData.email}</strong>
                        </p>

                        <div className="mb-6">
                            <label className="block text-lg text-gray-600 mb-2">Enter OTP</label>
                            <input
                                type="text"
                                name="otp"
                                value={formData.otp}
                                onChange={handleInputChange}
                                className="w-full border border-gray-300 rounded-lg p-3"
                            />
                        </div>

                        <button
                            type="button"
                            onClick={handleVerify}
                            className="w-full bg-customGreen text-white py-3 rounded-lg hover:bg-green-600"
                            disabled={loading} // Disable button while loading
                        >
                            {loading ? 'Verifying...' : 'Verify'}
                        </button>
                    </div>
                )}
            </div>
        </div>
    );
};

export default SignUpPage;
