import React, { useState } from 'react';

const EnterUniqueCode = () => {
    const [aadharFile, setAadharFile] = useState(null);
    const [panFile, setPanFile] = useState(null);
    const [uniqueCode, setUniqueCode] = useState('');

    const handleFileChange = (e, setFile) => {
        const file = e.target.files[0];
        if (file && file.type.startsWith('image/')) {
            setFile(file);
        } else {
            alert('Please upload a valid image file.');
        }
    };

    const handleSubmit = (e) => {
        e.preventDefault();
        // Submit logic to backend for verifying unique code
        alert(`Documents uploaded and unique code ${uniqueCode} verified!`);
        // After successful verification, tenant will see property details and confirm acceptance
    };

    return (
        <div className="container mx-auto py-10">
            <h1 className="text-4xl mb-6">Enter Unique Code</h1>
            <form onSubmit={handleSubmit}>
                <div className="mb-4">
                    <label className="block mb-2 text-lg">Upload Aadhar Card (Image)</label>
                    <input
                        type="file"
                        accept="image/*"
                        onChange={(e) => handleFileChange(e, setAadharFile)}
                        className="border p-2 w-full"
                    />
                </div>
                <div className="mb-4">
                    <label className="block mb-2 text-lg">Upload PAN Card (Image)</label>
                    <input
                        type="file"
                        accept="image/*"
                        onChange={(e) => handleFileChange(e, setPanFile)}
                        className="border p-2 w-full"
                    />
                </div>
                <div className="mb-4">
                    <label className="block mb-2 text-lg">Enter Unique Code</label>
                    <input
                        type="text"
                        value={uniqueCode}
                        onChange={(e) => setUniqueCode(e.target.value)}
                        className="border p-2 w-full"
                    />
                </div>
                <button
                    type="submit"
                    className="bg-customGreen text-white py-2 px-4 rounded"
                >
                    Submit
                </button>
            </form>
        </div>
    );
};

export default EnterUniqueCode;
