import React, { useState } from 'react';

const LoginPage = () => {
    const [formData, setFormData] = useState({
        email: '',
        password: ''
    });

    const [errors, setErrors] = useState({});

    // Simple validation function
    const validateInputs = () => {
        let valid = true;
        let newErrors = {};

        // Validate email
        const emailPattern = /^[^\s@]+@[^\s@]+\.[^\s@]+$/;
        if (!emailPattern.test(formData.email)) {
            newErrors.email = 'Email is not valid';
            valid = false;
        }

        // Validate password
        if (formData.password === '') {
            newErrors.password = 'Password is required';
            valid = false;
        }

        setErrors(newErrors);
        return valid;
    };

    const handleLogin = () => {
        if (validateInputs()) {
            // Login logic can go here (e.g., API request)
            alert('Login successful!');
        }
    };

    const handleInputChange = (e) => {
        setFormData({ ...formData, [e.target.name]: e.target.value });
    };

    return (
        <div className="bg-gray-100 py-10 min-h-screen">
            <div className="max-w-3xl mx-auto px-6">
                <div className="bg-white p-10 shadow-xl rounded-lg mb-10">
                    <h1 className="text-4xl text-customGreen font-bold mb-6 text-center">Login</h1>
                    <form>
                        <div className="mb-6">
                            <label className="block text-lg text-gray-600 mb-2">Email</label>
                            <input
                                type="email"
                                name="email"
                                value={formData.email}
                                onChange={handleInputChange}
                                className="w-full border border-gray-300 rounded-lg p-3"
                            />
                            {errors.email && <p className="text-red-500 text-sm">{errors.email}</p>}
                        </div>

                        <div className="mb-6">
                            <label className="block text-lg text-gray-600 mb-2">Password</label>
                            <input
                                type="password"
                                name="password"
                                value={formData.password}
                                onChange={handleInputChange}
                                className="w-full border border-gray-300 rounded-lg p-3"
                            />
                            {errors.password && <p className="text-red-500 text-sm">{errors.password}</p>}
                        </div>

                        <button
                            type="button"
                            onClick={handleLogin}
                            className="w-full bg-customGreen text-white py-3 rounded-lg hover:bg-green-600"
                        >
                            Login
                        </button>
                    </form>
                </div>
            </div>
        </div>
    );
};

export default LoginPage;
