import React, { useState } from 'react';
import axiosInstance from '../api/axios'; // Import Axios instance

const SignUpPage = () => {
    const [step, setStep] = useState(1); // To track which step the user is on
    const [formData, setFormData] = useState({
        firstName: '',
        lastName: '',
        phone: '',
        email: '',
        otp: '',
        password: ''
    });

    const [errors, setErrors] = useState({});

    // Simple validation function
    const validateInputs = () => {
        let valid = true;
        let newErrors = {};

        // Validate first name
        if (formData.firstName === '') {
            newErrors.firstName = 'First Name is required';
            valid = false;
        }

        // Validate last name
        if (formData.lastName === '') {
            newErrors.lastName = 'Last Name is required';
            valid = false;
        }

        // Validate phone (example: 10 digits)
        const phonePattern = /^[0-9]{10}$/;
        if (!phonePattern.test(formData.phone)) {
            newErrors.phone = 'Phone number must be 10 digits';
            valid = false;
        }

        // Validate email
        const emailPattern = /^[^\s@]+@[^\s@]+\.[^\s@]+$/;
        if (!emailPattern.test(formData.email)) {
            newErrors.email = 'Email is not valid';
            valid = false;
        }

        setErrors(newErrors);
        return valid;
    };

    // Function to handle Next Step (Send OTP)
    const handleNextStep = async () => {
        if (validateInputs()) {
            try {
                const response = await axiosInstance.post('auth/send-otp', {
                    firstName: formData.firstName,
                    lastName: formData.lastName,
                    phone: formData.phone,
                    email: formData.email,
                    userType: 'tenant' // or 'owner', modify based on selection
                });
                console.log(response.data);
                setStep(2); // Move to OTP step
            } catch (error) {
                console.log('Error sending OTP:', error.response.data.msg);
            }
        }
    };

    // Function to handle OTP Verification
    const handleVerify = async () => {
        try {
            const response = await axiosInstance.post('auth/verify-otp', {
                email: formData.email,
                otp: formData.otp,
            });
            console.log(response.data);
            setStep(3); // Move to password creation step
        } catch (error) {
            console.log('Error verifying OTP:', error.response.data.msg);
        }
    };

    // Function to handle Password Submission
    const handleSetPassword = async () => {
        try {
            const response = await axiosInstance.post('auth/set-password', {
                email: formData.email,
                password: formData.password,
            });
            console.log(response.data);
            alert('User registered successfully!');
        } catch (error) {
            console.log('Error setting password:', error.response.data.msg);
        }
    };

    const handleInputChange = (e) => {
        setFormData({ ...formData, [e.target.name]: e.target.value });
    };

    return (
        <div className="bg-gray-100 py-10 min-h-screen">
            <div className="max-w-3xl mx-auto px-6">
                {step === 1 ? (
                    // Step 1: Collect User Details
                    <div className="bg-white p-10 shadow-xl rounded-lg mb-10">
                        <h1 className="text-4xl text-customGreen font-bold mb-6 text-center">Sign Up</h1>
                        <form>
                            <div className="mb-6">
                                <label className="block text-lg text-gray-600 mb-2">First Name</label>
                                <input
                                    type="text"
                                    name="firstName"
                                    value={formData.firstName}
                                    onChange={handleInputChange}
                                    className="w-full border border-gray-300 rounded-lg p-3"
                                />
                                {errors.firstName && <p className="text-red-500 text-sm">{errors.firstName}</p>}
                            </div>

                            <div className="mb-6">
                                <label className="block text-lg text-gray-600 mb-2">Last Name</label>
                                <input
                                    type="text"
                                    name="lastName"
                                    value={formData.lastName}
                                    onChange={handleInputChange}
                                    className="w-full border border-gray-300 rounded-lg p-3"
                                />
                                {errors.lastName && <p className="text-red-500 text-sm">{errors.lastName}</p>}
                            </div>

                            <div className="mb-6">
                                <label className="block text-lg text-gray-600 mb-2">Phone Number</label>
                                <input
                                    type="text"
                                    name="phone"
                                    value={formData.phone}
                                    onChange={handleInputChange}
                                    className="w-full border border-gray-300 rounded-lg p-3"
                                />
                                {errors.phone && <p className="text-red-500 text-sm">{errors.phone}</p>}
                            </div>

                            <div className="mb-6">
                                <label className="block text-lg text-gray-600 mb-2">Email</label>
                                <input
                                    type="email"
                                    name="email"
                                    value={formData.email}
                                    onChange={handleInputChange}
                                    className="w-full border border-gray-300 rounded-lg p-3"
                                />
                                {errors.email && <p className="text-red-500 text-sm">{errors.email}</p>}
                            </div>

                            <button
                                type="button"
                                onClick={handleNextStep}
                                className="w-full bg-customGreen text-white py-3 rounded-lg hover:bg-green-600"
                            >
                                Next
                            </button>
                        </form>
                    </div>
                ) : step === 2 ? (
                    // Step 2: OTP Verification
                    <div className="bg-white p-10 shadow-xl rounded-lg mb-10">
                        <h1 className="text-4xl text-customGreen font-bold mb-6 text-center">OTP Verification</h1>
                        <p className="text-lg text-gray-600 mb-6 text-center">
                            An OTP has been sent to your email ID: <strong>{formData.email}</strong>
                        </p>

                        <div className="mb-6">
                            <label className="block text-lg text-gray-600 mb-2">Enter OTP</label>
                            <input
                                type="text"
                                name="otp"
                                value={formData.otp}
                                onChange={handleInputChange}
                                className="w-full border border-gray-300 rounded-lg p-3"
                            />
                        </div>

                        <button
                            type="button"
                            onClick={handleVerify}
                            className="w-full bg-customGreen text-white py-3 rounded-lg hover:bg-green-600"
                        >
                            Verify
                        </button>
                    </div>
                ) : (
                    // Step 3: Set Password
                    <div className="bg-white p-10 shadow-xl rounded-lg mb-10">
                        <h1 className="text-4xl text-customGreen font-bold mb-6 text-center">Set Password</h1>

                        <div className="mb-6">
                            <label className="block text-lg text-gray-600 mb-2">Password</label>
                            <input
                                type="password"
                                name="password"
                                value={formData.password}
                                onChange={handleInputChange}
                                className="w-full border border-gray-300 rounded-lg p-3"
                            />
                        </div>

                        <button
                            type="button"
                            onClick={handleSetPassword}
                            className="w-full bg-customGreen text-white py-3 rounded-lg hover:bg-green-600"
                        >
                            Set Password
                        </button>
                    </div>
                )}
            </div>
        </div>
    );
};

export default SignUpPage;
