import React, { useEffect, useState } from 'react';
import axiosInstance from './api/axios'; // Assuming axiosInstance is configured to handle your API requests

const SearchProperty = () => {
    const [properties, setProperties] = useState([]);
    const [errorMessage, setErrorMessage] = useState('');

    // Fetch properties when the component mounts
    useEffect(() => {
        const fetchProperties = async () => {
            try {
                // Fetch all properties that have an email attribute
                const response = await axiosInstance.get(`/properties/for-search`);
                setProperties(response.data); // Store the fetched properties
            } catch (error) {
                setErrorMessage('Error fetching properties');
                console.error('Error fetching properties:', error);
            }
        };
        fetchProperties(); // Call the fetch function when component mounts
    }, []);

    return (
        <div className="container mx-auto py-10">
            <h1 className="text-4xl font-bold mb-6">Available Properties</h1>

            {errorMessage ? (
                <div className="text-red-500">{errorMessage}</div>
            ) : (
                <div className="grid grid-cols-1 sm:grid-cols-2 md:grid-cols-3 gap-6">
                    {properties.map((property) => (
                        <div key={property._id} className="bg-white p-6 shadow rounded-lg">
                            <h2 className="text-xl font-bold">{property.address}</h2>
                            <p>Rent: ₹{property.rent}</p>
                            <p>Deposit: ₹{property.deposit}</p>
                            <button
                                className="bg-blue-500 text-white mt-4 py-2 px-4 rounded"
                                onClick={() => alert(`Interest shown in property: ${property._id}`)}
                            >
                                Show Interest
                            </button>
                        </div>
                    ))}
                </div>
            )}
        </div>
    );
};

export default SearchProperty;
