import React, { useState, useEffect } from 'react';
import TenantNavigation from './TenantNavigation'; // Custom navbar after login for tenant
import Footer from './Footer'; // Reusing the same footer
import { Link } from 'react-router-dom';

const TenantDashboardLayout = () => {
    const [loggedInUser, setLoggedInUser] = useState(null);
    const [rentReminders, setRentReminders] = useState([]);

    useEffect(() => {
        // Fetch logged-in user from localStorage
        const storedUser = JSON.parse(localStorage.getItem('user'));
        if (storedUser) {
            setLoggedInUser(storedUser);
        }

        // Simulating rent reminder data, replace with an API call if necessary
        const reminders = [
            { id: 1, dueDate: '2024-11-01', amount: 1500 },
            { id: 2, dueDate: '2024-12-01', amount: 1500 }
        ];
        setRentReminders(reminders);
    }, []);

    return (
        <div>
            <TenantNavigation /> {/* Tenant-specific navbar after login */}
            <div className="container mx-auto py-10">
                {/* Personalized Greeting */}
                <h1 className="text-4xl mb-6">
                    Welcome, {loggedInUser ? loggedInUser.firstName : 'Tenant'} to your Dashboard!
                </h1>

                {/* Introduction about DocEase */}
                <div className="mb-10">
                    <p className="text-lg text-gray-700 mb-4">
                        DocEase is designed to make your renting experience smooth and hassle-free. 
                        You can easily search for properties available for rent, manage your rented properties, and stay on top of rent payments.
                    </p>
                </div>

                {/* Two Columns Layout: Search Property and Enter Unique Code */}
                <div className="grid grid-cols-2 gap-10">
                    {/* Search Property Section */}
                    <div className="bg-white shadow-lg p-6 rounded-lg">
                        <h2 className="text-2xl font-semibold mb-4">Search Property</h2>
                        <p className="text-lg mb-4">
                            Browse available rental properties. Use our platform to find your next home quickly and easily.
                        </p>
                        <Link to="/search-property">
                            <button className="bg-customGreen text-white py-3 px-6 rounded-lg hover:bg-green-600">
                                Search Property
                            </button>
                        </Link>
                    </div>

                    {/* Enter Unique Code Section */}
                    <div className="bg-white shadow-lg p-6 rounded-lg">
                        <h2 className="text-2xl font-semibold mb-4">Enter Unique Code</h2>
                        <p className="text-lg mb-4">
                            Enter the unique code provided by your property owner to access details about the property and start the rental process.
                        </p>
                        <Link to="/enter-unique-code">
                            <button className="bg-customGreen text-white py-3 px-6 rounded-lg hover:bg-green-600">
                                Enter Unique Code
                            </button>
                        </Link>
                    </div>
                </div>

                {/* Rent Payment Reminders Section */}
                <div className="mt-10">
                    <h2 className="text-2xl mb-4">Upcoming Rent Payments</h2>
                    {rentReminders.length > 0 ? (
                        <ul>
                            {rentReminders.map(reminder => (
                                <li key={reminder.id} className="mb-2">
                                    Rent due on {reminder.dueDate} for ${reminder.amount}
                                </li>
                            ))}
                        </ul>
                    ) : (
                        <p>No upcoming rent payments.</p>
                    )}
                </div>
            </div>
            <Footer /> {/* Reusing the same footer */}
        </div>
    );
};

export default TenantDashboardLayout;
