import React, { useEffect, useState } from 'react';
import OwnerNavigation from './OwnerNavigation'; // Custom owner navigation
import Footer from './Footer'; // Reusing the same footer
import { Link } from 'react-router-dom';

const OwnerDashboardLayout = () => {
    const [loggedInUser, setLoggedInUser] = useState(null);

    // Fetch logged-in user from localStorage on mount
    useEffect(() => {
        const storedUser = JSON.parse(localStorage.getItem('user'));
        if (storedUser) {
            setLoggedInUser(storedUser);
        }
    }, []);

    return (
        <div>
            <OwnerNavigation /> {/* Custom navigation for the owner */}
            <div className="container mx-auto py-10">
                {/* Personalized Greeting */}
                <h1 className="text-4xl mb-6">
                    Welcome, {loggedInUser ? loggedInUser.firstName : 'Owner'}!
                </h1>
                <p className="text-lg mb-10">You are logged in as <strong>Owner</strong>. Manage your properties and tenants from here.</p>

                {/* Add Property Button */}
                <Link to="/add-property">
                    <button className="bg-customGreen text-white py-3 px-6 rounded-lg hover:bg-green-600 mb-4">
                        Add Property
                    </button>
                </Link>

                {/* Tenant Management Button */}
                <Link to="/tenant-management">
                    <button className="bg-customGreen text-white py-3 px-6 rounded-lg hover:bg-green-600 ml-4">
                        Tenant Management
                    </button>
                </Link>
            </div>
            <Footer /> {/* Reusing the same footer */}
        </div>
    );
};

export default OwnerDashboardLayout;
